def knight_min_health(dungeon):
    m, n = len(dungeon), len(dungeon[0])
    dp = [[0] * n for _ in range(m)]
    dp[0][0] = dungeon[0][0]

    for i in range(m):
        for j in range(n):
            if i > 0:
                dp[i][j] += dp[i-1][j]
            if j > 0:
                dp[i][j] += dp[i][j-1]
            if i < m - 1:
                dp[i][j] += max(0, dungeon[i+1][j] - 1)
            if j < n - 1:
                dp[i][j] += max(0, dungeon[i][j+1] - 1)
    return dp[m-1][n-1]

