import math

def distance(x1, y1, x2, y2):
    return math.sqrt((x1 - x2)**2 + (y1 - y2)**2)

def collision(x1, y1, vx, vy, r1, x2, y2, vx2, vy2, r2):
    dist1 = distance(x1, y1, x2, y2)
    dist2 = distance(vx, vy, vx2, vy2)
    return dist1 + dist2 > r1 + r2

def agglomerate(x1, y1, vx, vy, r1, x2, y2, vx2, vy2, r2):
    dist1 = distance(x1, y1, x2, y2)
    dist2 = distance(vx, vy, vx2, vy2)
    return dist1 + dist2 <= r1 + r2

def solve():
    n = int(input())
    droplets = []
    for i in range(n):
        x, y, vx, vy, r = map(int, input().split())
        droplets.append((x, y, vx, vy, r))

    while True:
        new_droplets = []
        for i in range(n):
            for j in range(i + 1, n):
                x1, y1, vx1, vy1, r1 = droplets[i]
                x2, y2, vx2, vy2, r2 = droplets[j]

                if collision(x1, y1, vx1, vy1, r1, x2, y2, vx2, vy2, r2):
                    r1_new = r1 + distance(x1, y1, x2, y2) / (r1 + r2)
                    x1_new = (x1 - vx1 * r1 * distance(x1, y1, x2, y2) / (r1 + r2)) / vx1
                    y1_new = (y1 - vy1 * r1 * distance(x1, y1, x2, y2) / (r1 + r2)) / vy1

                    r2_new = r2 - distance(x1, y1, x2, y2) / (r1 / r2 + r2 / r1)
                    x2_new = (x2 - vx2 * r2 * distance(x1, y1, x2, y2) / (r1 / r2 + r2 / r1)) / vx2
                    y2_new = (y2 - vy2 * r2 * distance(x1, y1, x2, y2) / (r1 / r2 + r2 / r1)) / vy2

                    new_droplets.append((x1_new, y1_new, vx1_new, vy1_new, r1_new))
                    new_droplets.append((x2_new, y2_new, vx2_new, vy2_new, r2_new))

        droplets = new_droplets

    k = len(droplets)
    t = 0.0

    for i in range(k):
        x1, y1, vx1, vy1, r1 = droplets[i]
        for j in range(i + 1, k):
