    dp = {}

    def find_max_yellow(n):
        if n in dp:
            return dp[n]

        if n == 1:
            return 1
        if n == 2:
            return 2
        if n == 3:
            return 4

        max_yellow = 0

        if n % 2 == 0:
            # Even level
            left_children = find_max_yellow(n - 1)
            right_children = find_max_yellow(n - 1)
            max_yellow = left_children + right_children + (n - left_children - right_children)
            max_yellow %= mod

            # Claw: {1, 2, 3, 4}
            yellow = 4
            for i in range(4):
                yellow += find_max_yellow(n - 1) - 1
                yellow %= mod
            max_yellow = max(max_yellow, yellow)

        else:
            # Odd level
            max_yellow = 0

        dp[n] = max_yellow
        return max_yellow

    print(find_max_yellow(n))

