def solve():
    n, k = map(int, input().split())

    def gcd(a, b):
        if b == 0:
            return a
        return gcd(b, a % b)

    def calculate_probability(n, k):
        if n == 1:
            return 1, 1

        days_in_year = 2 ** n

        def combinations(n, k):
            if k < n - k:
                k = n - k
            res = 1
            for i in range(k):
                res = res * (n - i) // (i + 1)
            return res

        total_combinations = combinations(n, k)

        if total_combinations > (days_in_year * (days_in_year - 1)) // 2:
            return 1, (days_in_year * (days_in_year - 1)) // 2

        def calculate_fraction(n, k):
            if n == 1:
                return 1, 1

            days = 2 ** n

            def find_common(birthday_days):
                count = 0
                for i in range(1, k + 1):
                    if birthday_days % (i + 1) == 0:
                        count += 1
                return count

            common_count = find_common(days)

            if common_count >= k:
                return 1, 1

            probability = 1
            for i in range(1, k):
                if i == 1:
                    probability = (days - (days // (k + 1)) * (days // (k + 1) - 1)) // 2
                else:
                    probability = (days - (days // (k + 1)) * (days // (k + 1) - 1)) // 2 / (k - 1)

            return probability, (probability * days // (n * (n - 1))) % (10**6 + 3)

        a, b = calculate_fraction(n, k)
        a %= (10**6 + 3)
        b %= (10**6 + 3)

        return a, b

    a, b = solve()
    print(a, b)

