def next_permutation(arr):
    for i in range(len(arr) - 2, -1, -1):
        if arr[i] < arr[i+1]:
            break
    else:
        arr.reverse()
        return

    pivot = arr[-1]
    for j in range(len(arr) - 1, i, -1):
        if arr[j] > pivot:
            break
    arr.pop(i)
    arr.insert(i, pivot)
    arr.pop()
    return arr

