        visited = set()
        for x, y, color in desires:
            if (x, y) in visited or color == 'B':
                return False
            visited.add((x, y))
        return True

    def dfs(desires, visited):
        max_satisfied = 0
        for x, y, color in desires:
            if (x, y) in visited:
                continue

            satisfied = 0
            if color == 'B':
                satisfied = 1
            else:
                satisfied = 0

            max_satisfied = max(max_satisfied, satisfied)

            # Add adjacent squares to the visited set
            if x > 0:
                adjacent_x = x - 1
                if adjacent_x >= 0 and (adjacent_x, y) not in visited:
                    satisfied = max(satisfied, 1 + dfs(desires, [adjacent_x, y]))
            if x < k - 1:
                adjacent_x = x + 1
                if adjacent_x < k and (x, y) not in visited:
                    satisfied = max(satisfied, 1 + dfs(desires, [x, y]))
            if y > 0:
                adjacent_y = y - 1
                if adjacent_y >= 0 and (x, adjacent_y) not in visited:
                    satisfied = max(satisfied, 1 + dfs(desires, [x, adjacent_y]))
            if y < k - 1:
                adjacent_y = y + 1
                if adjacent_y < k and (x, y) not in visited:
                    satisfied = max(satisfied, 1 + dfs(desires, [x, adjacent_y]))

        return max_satisfied

    max_satisfied = 0
    for x, y, color in desires:
        if (x, y) not in visited:
