def solve():
    n, k = map(int, input().split())

    if k < n // 2 or k == 0:
        print("-1")
        return

    matrix = [[0] * n for _ in range(n)]

    def is_max(matrix, row, col, direction):
        if direction == 0:  # Up
            if row == 0:
                return True
            if col > 0:
                return matrix[row][col - 1] >= 1
            else:
                return False
        else:  # Down
            if row == n - 1:
                return True
            if col > 0:
                return matrix[row + 1][col - 1] >= 1
            else:
                return False

    def find_max(matrix, row, col, direction):
        nonlocal best_row, best_col

        if direction == 0:  # Up
            if row == 0:
                if is_max(matrix, row, col, 0):
                    col -= 1
                    if col == 0 and is_max(matrix, row, col, 1):
                        col += 1
                    find_max(matrix, row, col, 1)
                    return
            else:
                if is_max(matrix, row, col, 0):
                    col -= 1
                    if col == 0 and is_max(matrix, row, col, 1):
                        col += 1
                    find_max(matrix, row, col, 1)
                    return
        else:  # Down
            if row == n - 1:
                if is_max(matrix, row, col, 1):
                    col += 1
                    if col == n and is_max(matrix, row, col, 0):
                        col -= 1
                        find_max(matrix, row, col, 0)
                        return
            else:
                if is_max(matrix, row, col, 1):
                    col += 1
                    if col == n and is_max(matrix, row, col, 0):
                        col -= 1
                        find_max(matrix, row, col, 0)
                        return
        best_row = row
        best_col = col

    find_max(matrix, 0, n // 2, 0)

    for i in range(n // 2):
        matrix[0][i] = 1
        matrix[i][0] = 1

    for i in range(n // 2, n - 1 - n % 2):
        matrix[i][n - 1 - n % 2] = 1
        matrix[n - 1 - n % 2][i] = 1

    for i in range(n // 2):
        matrix[i][n // 2] = 1
        matrix[n // 2][i] = 1

    for i in range(n // 2 + 1, n):
        matrix[i][0] = 1
        matrix[0][i] = 1

    for i in range(1, n // 2 + 1):
        matrix[n - 1 - i][n // 2] = 1
        matrix[n - 1 - i][0] = 1

    print(*matrix)

