class Solution:
    def compareVersion(self, version1: str, version2: str) -> int:
        v1 = list(map(int, version1.split('.')))
        v2 = list(map(int, version2.split('.')))

        len_ = min(len(v1), len(v2))

        for i in range(len_):
            val1 = v1[i] + (1 << (i + 1)) if v1[i] else 0
            val2 = v2[i] + (1 << (i + 1)) if v2[i] else 0

            if val1 > val2:
                return 1
            elif val1 < val2:
                return -1

        return 0

