import math

def dot(u, v):
    return u[0]*v[0] + u[1]*v[1]

def cross(u, v):
    return (u[1]*v[2] - u[2]*v[1],
            u[2]*v[0] - u[0]*v[2],
            u[0]*v[1] - u[1]*v[0])

def magnitude(u):
    return math.sqrt(dot(u, u))

def angle(u, v):
    return math.acos(dot(u, v) / (magnitude(u) * magnitude(v)))

def opposite(u, v):
    return (u[0]*v[1] - u[1]*v[0],
            u[1]*v[2] - u[2]*v[1],
            u[2]*v[0] - u[0]*v[2],
            u[0]*v[1] - u[1]*v[0])

def closest(u, v):
    angle_sum = angle(u, v) + math.pi - angle(opposite(u, v), v)
    return angle_sum, v[0] + v[1] + v[2]

