def solve():
    n, q = map(int, input().split())
    a = list(map(int, input().split()))

    def reverse_k(k):
        nonlocal a
        left = 0
        right = len(a) - 1
        while left <= right:
            mid = (left + right) // 2
            if mid % 2 == k % 2:
                if a[mid] < a[right]:
                    right -= 1
                else:
                    left = mid + 1
            else:
                if a[mid] < a[right]:
                    right -= 1
                else:
                    left = mid + 1
        a[mid] = a[right]

    def swap_k(k):
        nonlocal a
        left = 0
        right = len(a) - 1
        while left <= right:
            mid = (left + right) // 2
            if mid % 2 == k % 2:
                if mid < len(a) / 2:
                    a[mid], a[mid + len(a) / 2] = a[mid + len(a / 2)], a[mid]
                    left += 1
                else:
                    a[mid], a[mid - len(a) / 2] = a[mid - len(a / 2)], a[mid]
                    right -= 1
            else:
                if mid < len(a) / 2:
                    a[mid], a[mid + len(a) / 2 - 1] = a[mid + len(a / 2 - 1)], a[mid - 1]
                    right -= 1
                else:
                    a[mid], a[mid - len(a) / 2] = a[mid - len(a / 2)], a[mid + 1]
                    left += 1
        a[k], a[k - len(a) / 2] = a[k - len(a / 2)], a[k]

    def sum_l_r(l, r):
        s = 0
        for i in range(len(a)):
            if (i >= l) & (i <= r):
                s += a[i]
        return s

    for _ in range(q):
        query = list(map(int, input().split()))
        if query[0] == 1:
            x = query[1] - 1
            k = query[2]
            a[x] = query[k]
        elif query[0] == 2:
            k = query[1]
            reverse_k(k)
        elif query[0] == 3:
            k = query[1]
            swap_k(k)
        elif query[0] == 4:
            l = query[1] - 1
            r = query[2] - 1
            print(sum_l_r(l, r))

solve()

