    def is_valid(sequence):
        current_room = 0
        for i in range(n):
            next_room = sequence[i]
            if next_room < 0 or next_room >= n:
                return False
            if not (0 <= next_room < n):
                return False
            if sequence[current_room] == next_room:
                current_room = next_room
            elif current_room == next_room:
                return False

        return True

    def backtrack(current_room, path):
        if current_room == n - 1:
            if is_valid(path):
                print("Yes")
                return True
            else:
                print("Impossible")
                return False

        for i in range(n):
            if not (0 <= i < n):
                continue
            if senior_config[current_room][i] == 0:
                continue

            new_path = path + [i + 1]
            if backtrack(i + 1, new_path):
                return True

        return False

    if backtrack(0, []):
        print("Yes")
    else:
        print("No")

