        neighbors = []
        if direction == "L":
            neighbors.append((r - 1, c))
            if not is_valid(r - 1, c, "L"):
                neighbors.append((r, c + 1))
            if not is_valid(r - 1, c + 1, "L"):
                neighbors.append((r, c + 2))
        elif direction == "R":
            neighbors.append((r + 1, c))
            if not is_valid(r + 1, c, "R"):
                neighbors.append((r, c - 1))
            if not is_valid(r + 1, c - 1, "R"):
                neighbors.append((r, c - 2))
        elif direction == "U":
            neighbors.append((r, c - 1))
            if not is_valid(r, c - 1, "U"):
                neighbors.append((r - 1, c))
            if not is_valid(r - 1, c - 1, "U"):
                neighbors.append((r - 1, c + 1))
        elif direction == "D":
            neighbors.append((r, c + 1))
            if not is_valid(r, c + 1, "D"):
                neighbors.append((r + 1, c))
            if not is_valid(r + 1, c + 1, "D"):
                neighbors.append((r + 2, c))

        return neighbors

