        visited = set()
        queue = [(0, 0, "")]
        directions = {"L": (-1, 0), "R": (1, 0), "U": (0, -1), "D": (0, 1)}

        while queue:
            r, c, path = queue.pop(0)

            if (r, c, path) in visited:
                continue

            visited.add((r, c, path))

            if r == 0 and c == 0:
