                neighbors = get_neighbors(nr, nc, dr)

                for nr_n, nc_n in neighbors:
                    if (nr_n, nc_n, neighbor_path + dr + dc) not in visited:
                        queue.append((nr_n, nc_n, neighbor_path + dr + dc))

