        moves = []
        if x > 0:
            moves.append((x, y))
        if x < 0:
            moves.append((abs(x), y))
        if y > 0:
            moves.append((x, y))
        if y < 0:
            moves.append((x, -y))
        return moves

    def is_valid(pos, moves):
        new_pos = pos[:]
        for move in moves:
            new_pos[0] += move[0]
            new_pos[1] += move[1]
        return new_pos == (x2, y2)

    def find_path(start_x, start_y, end_x, end_y, current_moves, current_pos):
        if current_pos == (end_x, end_y):
            return is_valid(current_pos, current_moves)

        moves = calculate_moves(start_x, start_y, px, py)
        for move in moves:
            new_pos = current_pos[:]
            new_pos[0] += move[0]
            new_pos[1] += move[1]
            if find_path(move[0], move[1], end_x, end_y, current_moves + [(move[0], move[1])], new_pos):
                return True

