def solve():
    n, k = map(int, input().split())
    w = list(map(int, input().split()))

    MOD = 10**9 + 7

    def calculate_partition_sum(weights, num_subsets):
        if num_subsets == 1:
            return sum(weights)

        partition_sum = 0

        for i in range(1 << (n - num_subsets + 1)):
            subset_indices = []
            subset_weights = []

            for j in range(n - num_subsets + 1):
                if (i >> j) & 1:
                    subset_indices.append(j + 1)
                    subset_weights.append(weights[j])

            if not subset_indices:
                continue

            num_subsets_in_current_partition = 1
            current_subset_weight = 0

            for j in range(len(subset_indices)):
                current_subset_weight += subset_weights[j]
                num_subsets_in_current_partition += 1

                if num_subsets_in_current_partition == num_subsets:
                    partition_sum += current_subset_weight
                    current_subset_weight = 0
                    num_subsets_in_current_partition = 1

            partition_sum += calculate_partition_sum(subset_weights, num_subsets_in_current_partition)

        return partition_sum % MOD

    print(calculate_partition_sum(w, k))

