        adj[a-1].append(b-1)
        adj[b-1].append(a-1)

    visited = [False] * n
    components = []
    for i in range(n):
        if not visited[i]:
            component = []
            curr = i
            visited[i] = True
            while curr != -1:
                component.append(curr)
                curr = adj[curr].pop()

            components.append(component)

    total_extra = 0
    for component in components:
        original_capacity = 0
        for node in component:
            original_capacity += adj[node].count(-1)

        # Find the minimum capacity in the component
        min_capacity = float('inf')
        for node in component:
            if adj[node].count(-1) < min_capacity:
                min_capacity = adj[node].count(-1)

        # Calculate the extra capacity needed
        for node in component:
            adj[node].clear()
        for node in component:
            if adj[node].count(-1) > 0:
                adj[node].pop()
        for i in range(len(component)):
            if component[i] != -1:
                total_extra += min_capacity - (component[i] - 1)

