        for dist in distances:
            if is_prime(dist):
                temp_position = position + dist
                if temp_position < max_position:
                    max_position = temp_position
                    max_size = distances[dist - 1]
                elif temp_position == max_position:
                    max_size = max(max_size, distances[dist - 1])
        return max_size, max_position

