def solve():
    n, m, b = map(int, input().split())
    blocks = []
    for _ in range(b):
        blocks.append(list(map(int, input().split())))

    def is_valid(r, c, direction):
        if direction == '<':
            if r == 1 and c == n:
                return False
            if r == n and c > 0:
                return False
            if r > 1 and c == 0:
                return False
        elif direction == '>':
            if r == 1 and c < m:
                return False
            if r == n and c < 0:
                return False
            if r > n and c > m - 1:
                return False
        elif direction == '^':
            if r == 1 and c > 0:
                return False
            if r == n and c == m - 1:
                return False
            if r > n and c < m - 1:
                return False
        elif direction == 'v':
            if r == 1 and c < m:
                return False
            if r == n and c > 0:
                return False
            if r > n and c > m - 1:
                return False
        return True

    def find_path(start_r, start_c, end_r, end_c, current_blocks, visited):
        if start_r == end_r and start_c == end_c:
            path = []
            curr = [start_r, start_c]
            while curr[0] != start_r or curr[1] != start_c:
                path.append((curr[0], curr[1]))
                if curr[0] == start_r:
                    curr[0] -= 1
                elif curr[1] == start_c:
                    curr[1] -= 1
                curr = [start_r, start_c]
            path.append((start_r, start_c))
            return path

        if not (1 <= start_r <= n and 1 <= start_c <= m):
            return None

        if not (1 <= end_r <= n and 1 <= end_c <= m) or start_r > end_r or start_c > end_c:
            return None

        if current_blocks[start_r][start_c] == 1:
            return None

        visited[start_r, start_c] = True

        directions = ['<', '>', '^', 'v']
        for direction in directions:
            new_r = start_r
            new_c = start_c
            if direction == '<':
                new_r -= 1
            elif direction == '>':
                new_r += 1
            elif direction == '^':
                new_c -= 1
            else:
                new_c -= 1
            if is_valid(new_r, new_c, direction) and not visited[new_r, new_c]:
                path = find_path(new_r, new_c, end_r, end_c, current_blocks, visited)
                if path:
                    return path + [(new_r, new_c)]

        visited[start_r, start_c] = False
        return None

    def backtrack(r, c, current_blocks, visited):
        if r == n and c == m:
            path = find_path(1, 1, n, m, current_blocks, visited)
            if path:
                print(' '.join(path))
                for move in path:
                    print(move)
                return

        if not (1 <= r <= n and 1 <= c <= m) or not visited[r][c]:
            blocks[r][c] = 1
            current_blocks[r][c] = 1
            visited[r][c] = True
            backtrack(r + 1, c, current_blocks, visited)
            backtrack(r, c + 1, current_blocks, visited)
            blocks[r][c] = 0
            current_blocks[r][c] = 0
            visited[r][c] = False
            backtrack(r - 1, c, current_blocks, visited)
            backtrack(r, c - 1, current_blocks, visited)

    backtrack(1, 1, blocks, [[0 for _ in range(n)] for _ in range(m)])


