def distance(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)

def pan(grip1, grip2):
    return distance(grip1, grip2[0]) + distance(grip2, grip1[0])

def zoom(grip1, grip2):
    return distance(grip1, grip2[0]) - distance(grip2, grip1[0])

def rotate(grip1, grip2):
    touch1 = [p for p in grip1 if p in grip2]
    touch2 = [p for p in grip2 if p in grip1]

    if len(touch1) == 0 or len(touch2) == 0:
        return 0

    avg1 = sum(p for p in touch1) / len(touch1)
    avg2 = sum(p for p in touch2) / len(touch2)

    return math.acos((avg1 - avg2) / (math.sqrt(len(touch1)**2 + len(touch2)**2) - 1))

