        # Move down
        if row + 1 < n:
            ways = (ways + count_ways(row + 1, col, True)) % mod

        # Move up
        if row - 1 >= 0:
            ways = (ways + count_ways(row - 1, col, True)) % mod

        dp[(row, col, rev)] = ways
        return ways

