            max_expected = expected
            best_bit = k_bit
            expected_positions = v - (bin(k_bit).count('1') - 1)
        elif expected == max_expected and v > expected_positions:
            best_bit = k_bit

    print(expected_positions ^ (1 << (k - 1)))

