    n, m = map(int, input().split())
    mod = 1000000007

    roads = []
    for _ in range(m):
        roads.append(list(map(int, input().split())))

    adj = [[] for _ in range(n + 1)]
    for u, v, w in roads:
        adj[u].append((v, w))
        adj[v][0] = (u, w)

    dist = [[float('inf')] * (n + 1) for _ in range(2)]

    def dijkstra(start, end):
        dist[0][start] = 0
        dist[1][end] = 0

        pq = [(0, start)]

        while pq:
            d, u = pq.pop(0)

            if dist[0][u] < d or dist[1][u] < d:
                continue

            for v, w in adj[u]:
                if dist[0][v] > dist[0][u] + w:
                    dist[0][v] = dist[0][u] + w
                    pq.append((dist[0][v], v))
                elif dist[0][v] == dist[0][u] + w:
                    dist[0][v] = min(dist[0][v], dist[0][u] + w)

            if dist[1][u] < d:
                pq.append((dist[1][u], u))

        return dist[0], dist[1]

    for u, v, w in roads:
        dist = dijkstra(u, v)
        print((dist[0] % mod) * pow(dist[1] % mod, mod - 2, mod) % mod)

solve()

