def solve():
    n = int(input())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u, v))

    def bfs(start):
        visited = [False] * n
        queue = [(start, 0)]
        visited[start] = True

        min_dist = float('inf')

        while queue:
            node, dist = queue.pop(0)

            if dist > 2:
                min_dist = min(min_dist, dist)

            for neighbor, edge_type in edges:
                if not visited[neighbor] and edge_type == 0:
                    queue.append((neighbor, dist + 1))
                    visited[neighbor] = True

        return min_dist

    low = [0] * n
    disc = [0] * n
    parent = [-1] * n

    def find(node):
        if low[node] == disc[node]:
            return node
        low[node] = low[node] + 1
        parent[node] = find(parent[node])
        return parent[node]

    def union(node1, node2):
        root1 = find(node1)
        root2 = find(node2)

        if root1 != root2:
            if disc[root1] > disc[root2]:
                parent[root2] = root1
            else:
                parent[root1] = root2
                if disc[root1] == disc[root2]:
                    disc[root2] += 1

    start = 0
    add_edges = 0

    visited = [False] * n
    for node in range(1, n):
        if not visited[node]:
            dist = bfs(node)
            if dist <= 2:
                add_edges += 1
            else:
                union(node, 0)

    print(add_edges)

