def solve():
    n, m = map(int, input().split())
    a = list(map(int, input().split()))
    p = list(map(int, input().split()))

    def can_sort(arr):
        for i in range(n - 1):
            if arr[i] > arr[i + 1]:
                return False
        return True

    def allowed_swaps(arr, pos1, pos2):
        swaps = []
        for i in range(n):
            if i == pos1 - 1 and i != pos2 - 1:
                swaps.append(i + 1)
            elif i == pos2 - 1 and i != pos1 - 1:
                swaps.append(i + 1)
        return swaps

    def is_valid(arr, swaps):
        new_arr = arr[:]
        for swap in swaps:
            new_arr[swap - 1], new_arr[swap - 2] = new_arr[swap - 2], new_arr[swap - 1]
        return can_sort(new_arr)

    def find_solution(arr, p):
        for pos in p:
            if pos - 1 < n - 1 and pos != pos - 1:
                if find_solution(arr, allowed_swaps(arr, pos - 1, pos + 1)):
                    return True
            elif pos - 1 == n - 1 and pos != pos - 1:
                if find_solution(arr, allowed_swaps(arr, pos - 1, pos + 1)):
                    return True
        return False

    if find_solution(a, p):
        print("YES")
    else:
        print("NO")

