def solve():
    n, m, k = map(int, input().split())
    grid = []
    for _ in range(n):
        grid.append(list(map(int, input().split())))

    def calculate_paths(row, col, xors, memo):
        if row == n - 1 and col == m - 1:
            if xors == 0:
                return 1
            elif xors % 2 == 0:
                return memo[(row, col)] * calculate_paths(row + 1, col, xors // 2, memo)
            else:
                return memo[(row, col)] * calculate_paths(row + 1, col, (xors + 1) // 2, memo)
        if (row, col) in memo:
            return memo[(row, col)]
        for i in range(row + 1):
            for j in range(col + 1):
                new_xors = xors ^ grid[i][j]
                if i > 0:
                    new_xors ^= grid[i - 1][j]
                if j > 0:
                    new_xors ^= grid[i][j - 1]
                result = calculate_paths(row, col + 1, new_xors, memo)
                memo[(row, col)] = result
        return memo[(row, col)]

    ans = 0
    for i in range(n):
        for j in range(m):
            ans += calculate_paths(i, j, k, {})
    print(ans)

solve()

