def solve():
    a, b, k = map(int, input().split())

    result = []
    for i in range(a, b + 1):
        nums = sorted(list(range(i)))
        nums = sorted(nums, reverse=True)

        if i - nums[0] <= k:
            result.append(i)
        if i - nums[-(k+1)] >= k:
            result.append(i)

    print(*result)

solve()
