    def combinations(n, k):
        if k < 0 or k > n:
            return 0
        if k == 0 or k == n:
            return 1
        if k > n // 2:
            k = n - k

        res = 1
        for i in range(k):
            res = (res * (n - i)) % mod
            res = (res * pow(i + 1, mod - 2, mod)) % mod
        return res

    ans = 1
    for i in range(1, n + 1):
        ans = (ans * combinations(n, i)) % mod

    print(ans)

