import math

def distance(x1, y1, x2, y2):
    return math.sqrt((x2 - x1)**2 + (y2 - y1)**2)

def update(state, x, y, vx, vy, r):
    nx = (x + vx) / 2
    ny = (y + vy) / 2
    rx = (x - vx) / 2
    ry = (y - vy) / 2
    area = math.pi * r**2
    nxr = (area + rx * rx + area - nx * nx) / (2 * area)
    nyr = (area + ny * ny + area - ny * ny) / (2 * area)
    state[0] = nx * state[0] + ny * state[1] + nxr * state[0] + nyr * state[1]
    state[1] = nx * state[0] + ny * state[1] + nxr * state[1] + nyr * state[0]

def agglomerate(state, x, y, vx, vy, r):
    update(state, x, y, vx, vy, r)
    state[0] = state[0] / 2.0
    state[1] = state[1] / 2.0
    state[2] = vx
    state[3] = vy
    state[4] = vx * r + state[2]
    return state

def check_collision(state, x, y, vx, vy, r):
    update(state, x, y, vx, vy, r)
    dist = distance(state[0], state[1], 0, 0)
    if dist < 0.001:
        return True
    return False

def check_agglomeration(state, x, y, vx, vy, r):
    new_state = agglomerate(state, x, y, vx, vy, r)
    if check_collision(new_state, x, y, vx, vy, r):
        return True
    else:
        return False

def solve(state, t, max_t):
    if state[0] == state[1] and state[0] == state[2] and state[0] == state[3] and state[0] == state[4]:
        return state[0], float(t)
    if state[0] + t > max_t:
        return state[0], 0.0
    if check_agglomeration(state, 0, 0, state[2], state[3], state[4]):
        return state[0], t + state[2]
    new_state = agglomerate(state, 0, 0, state[2], state[3], state[4])
    return state[0], t + new_state[2]

