                    if unique_elements[i] == element:
                        prime_index = i
                        break
                if prime_index == -1:
                    moves += 1
                else:
                    moves += max(0, prime_count - (prime_index + 1))

        if min_moves > moves:
            min_moves = moves

    if min_moves == float('inf'):
        print(0)
