class Claw:
    def __init__(self, index, parent_index):
        self.index = index
        self.parent = parent_index
        self.children = []
        self.green = 0

    def is_claw(self):
        return self.parent == self.index and all(self.children.count(c) == 1 for c in self.children)

    def add_child(self, other):
        self.children.append(other.children[0])
        other.children = other.children[1:]

