def get_factors(m, n):
    factors = []
    i = 2
    while i * i <= n:
        if n % i == 0:
            factors.append(i)
            if i * i != n:
                factors.append(n // i)
        i += 1
    if n > 1:
        factors.append(n)
    return factors

def get_new_value(m, n):
    for k in range(1, 11):
        new_m = m
        for i in range(n):
            new_m = new_m * (k + 1)
        if new_m == m:
            return new_m
    return m

