def solve():
    k = int(input())

    def is_lunlun(num):
        if num.length < 10:
            return False

        for i in range(len(num) - 1):
            if abs(int(num[i]) - int(num[i+1])) > 1:
                return False
        return True

    def find_lunlun_number(length):
        num = ""
        num_digits = list(range(length + 1))

        # Fill the first digit
        num_digits[0] = str(length % 10 + 1)
        num = "".join(num_digits)

        if not is_lunlun(num):
            return length

        # Fill the rest of the digits
        for _ in range(length - 1):
            num_digits.sort()
            num = num_digits[0] + str(num_digits[1])
            num_digits.pop(0)
            num = "".join(num_digits)
            if not is_lunlun(num):
                return length + 1 + length // 10

        return length + 1 + length // 10 + length % 10 if length > 1 else length + 1

    print(find_lunlun_number(k - 1))

solve()

