        print(0)
        return

    ans = float('inf')

    def count_operations(n):
        if n == 1:
            return 0

        ans = float('inf')

        def get_shifts(num):
            s = bin(num)[2:]
            s = '0' * (3 - len(s)) + s

            shifts = []
            for i in range(len(s)):
                if s[i] == '1':
                    shifts.append(i)

            if not shifts:
                return 0

            num = int(s, 2)

            if num % 2 == 0:
                if len(shifts) == 1:
                    return 2
                else:
                    return 3
            else:
                if len(shifts) == 1:
                    return 1
                else:
                    return 4

        for shift in [0, 1, 2, 3]:
            new_num = num >> shift

            if get_shifts(new_num) < 12:
                ans = min(ans, count_operations(new_num) + 1)

        return ans

    try:
        ans = min([count_operations(a) for a in [a, b]])
    except ValueError:
        ans = -1

    print(ans)

