def solve():
    n, m = map(int, input().split())
    matrix = []
    for _ in range(n):
        matrix.append(list(input()))

    def count_ones(row, col):
        count = 0
        for i in range(row'size', row'size - col + 1 - 1, -1):
            for j in range(col'size', col'size - row'size + 1 - 1, -1):
                count += matrix[i][j]
        return count

    def check(row, col):
        if row'size + col'size > 20:
            return False
        for length in range(1, row'size + col'size + 1):
            if (row'size + col'size) % 2 == 0 and length % 2 == 0:
                continue
            if length % 2 == 1:
                start_row = row - (row'size - length + 1) // 2
                start_col = col - (col'size - length + 1) // 2
                if start_row < 0 or start_row >= row'size or start_col < 0 or start_col >= col'size:
                    continue
                if count_ones(start_row, start_col) % 2 == 0:
                    return False
        return True

    def find_min_changes():
        min_changes = float('inf')
        for row_start in range(1, n):
            for col_start in range(1, m):
                row_end = row_start + (n - row_start) // 2
                col_end = col_start + (m - col_start) // 2

                if row_end > n or col_end > m:
                    continue

                row_end = min(row_end, n)
                col_end = min(col_end, m)

                temp_matrix = [[0] * m for _ in range(row_end - row_start + 1)]
                for i in range(row_start, row_end):
                    for j in range(col_start, col_end):
                        temp_matrix[i - (row_start - 1)] += matrix[i][j]

                valid = check(row_start, col_start) and check(row_end - row_start + 1, col_end - col_start + 1)
                changes = count_ones(row_end - row_start + 1, col_end - col_start + 1) - temp_matrix.count(1)
                min_changes = min(min_changes, changes + valid - check(row_end - row_start + 1, col_end - col_start + 1))
        return min_changes

    if find_min_changes() == float('inf'):
        print("-1")
    else:
        print(find_min_changes())

solve()

