    n = s + m - 1

    graph = [[] for _ in range(n)]
    in_degree = [0] * n

    for i in range(m):
        for j in range(s):
            if preferences[i][j] != 0:
                graph[preferences[i][j] - 1].append(i)
                in_degree[i] += 1

    q = []
    for i in range(n):
        if in_degree[i] == 0:
            q.append(i)

    ans = []
    while q:
        i = q.pop(0)
        ans.append(i + 1)

        for j in graph[i]:
            in_degree[j] -= 1
            if in_degree[j] == 0:
                q.append(j)

    ans.sort()
    print(len(ans))
    print(*ans)

