class Solution:
    def compareVersion(self, version1: str, version2: str) -> int:
        v1 = self.parseVersion(version1)
        v2 = self.parseVersion(version2)
        while len(v1) > 0 and len(v2) > 0:
            v1_val = v1.pop() - 1
            v2_val = v2.pop() - 1
            if v1_val < v2_val:
                return -1
            elif v1_val > v2_val:
                return 1
        if len(v1) > 0:
            return -1
        elif len(v2) > 0:
            return 1
        else:
            return 0

    def parseVersion(self, version: str) -> int:
        v = 0
        i = 0
        while i < len(version):
            if version[i].isdigit():
                digit = 0
                while i < len(version) and version[i].isdigit():
                    digit = digit * 10 + (version[i] - '0')
                    i += 1
                v = v * 10 + digit
                i -= 1
            else:
                i += 1
        return v

