import math

def cross_product(vector1, vector2):
    return vector1[0]*vector2[1] - vector1[1]*vector2[0]

def magnitude(vector):
    return math.sqrt(vector[0]**2 + vector[1]**2)

def dot_product(vector1, vector2):
    return vector1[0]*vector2[0] + vector1[1]*vector2[1]

def angle(vector1, vector2):
    dot = dot_product(vector1, vector2)
    magnitude1 = magnitude(vector1)
    magnitude2 = magnitude(vector2)
    return math.acos(dot / (magnitude1 * magnitude2))

def opposite_direction(vector1, vector2):
    return [-vector1[0], -vector1[1], -vector2[0], -vector2[1]]

