def solve():
    n = int(input())
    passwords = []
    for _ in range(n):
        passwords.append(input())

    def is_equivalent(password1, password2):
        if len(password1) != len(password2):
            return False

        if password1 == password2:
            return True

        if len(set(password1)) != len(password1):
            return False

        if len(set(password2)) != len(password2):
            return False

        chars = set(password1)
        chars2 = set(password2)

        return not (chars.issubset(chars2) or chars2.issubset(chars))

    def find_access_point(passwords):
        visited = set()
        queue = [ passwords[0] ]

        while queue:
            current = queue.pop(0)
            if current in visited:
                continue

            visited.add(current)

            for i in range(len(current)):
                for next_char in list(current):
                    new_char = current[:i] + next_char + current[i+1:]
                    if is_equivalent(current, new_char):
                        return new_char

                    if len(new_char) > 0:
                        if new_char not in visited:
                            queue.append(new_char)

        return None

    access_point = find_access_point(passwords)

    if access_point is None:
        print(1)
    else:
        print(2 if access_point != passwords[0] else 1)

