    for i in range(n):
        graph[a[i] - ta].append(i)

    for i in range(m):
        graph[b[i] - ta].append(i + n)

    def dfs(node, visited, parent):
        visited[node] = True
        max_time = -1
        for neighbor in graph[node]:
            if not visited[neighbor]:
                max_time = max(max_time, dfs(neighbor, visited, node))
            elif neighbor != parent:
                max_time = max(max_time, dfs(neighbor, visited, -1))
        if parent != -1 and max_time >= ta + b[parent-1]:
            return max_time
        return ta + max_time

    max_time = -1
    for i in range(1, n + m + 1):
        if not i in [0] + list(visited):
            visited = [False] * (i + n + 1)
            max_time = max(max_time, dfs(i, visited, -1))
    print(max_time if max_time != -1 else -1)

