    n = int(input())
    frogs = []
    for _ in range(n):
        x, d = map(int, input().split())
        frogs.append((x, d))

    def calculate_tower_size(position, distances):
        total_distance = 0
        for dist in distances:
            total_distance += dist
        return total_distance

    def is_prime(n):
        if n <= 1:
            return False
        if n <= 3:
            return True
        if n % 2 == 0 or n % 3 == 0:
            return False
        i = 5
        while i * i <= n:
            if n % i == 0 or n % (i + 2) == 0:
                return False
            i += 6
        return True

    def find_best_position(current_position, remaining_frogs, remaining_distances):
        best_position = float('inf')
        best_size = 0

        for frog_position in range(current_position, -1, -1):
            for jump in remaining_distances:
                new_distances = []
                for dist in remaining_distances:
                    if dist % jump == 0 and is_prime(dist % jump):
                        new_distances.append(dist - jump)

                if not new_distances:
                    continue

                if calculate_tower_size(frog_position, new_distances) > best_size:
                    best_size = calculate_tower_size(frog_position, new_distances)
                    best_position = frog_position
                elif calculate_tower_size(frog_position, new_distances) == best_size:
                    best_position = min(best_position, frog_position)

        return best_position, best_size

    position = 0
    distances = []
    for frog in frogs:
        position, jump = frog
        remaining_frogs = []
        remaining_distances = []
        for other_frog in frogs:
            if other_frog != frog:
                other_position, _ = other_frog
                if other_position > position and is_prime(other_position - jump):
                    remaining_frogs.append(other_position)
                    remaining_distances.append(other_position - jump)

        best_position, best_size = find_best_position(position, remaining_frogs, remaining_distances)

        position = best_position

    print(position, best_size)
