    n, m = map(int, input().split())
    cities = [input() for _ in range(n)]
    edges = []
    for _ in range(m):
        u, v, cost = input().split()
        edges.append((u, v, int(cost)))
    assignments = []
    for _ in range(4):
        u, v = input().split()
        assignments.append((u, v))

    min_cost = float('inf')

    def calculate_cost(assignment):
        cost = 0
        for u, v in assignment:
            if not any(c cities in [u, v] for c in cities):
                continue
            if not any(c cities in [v, u] for c in cities):
                continue

            routes = []
            for x, y, cost_val in edges:
                if x == u or x == v or y == u or y == v:
                    routes.append((x, y, cost_val))

            routes.sort(key=lambda x: x[2])

            if routes[0][0] == u and routes[0][1] == v:
                cost += routes[0][2]
            elif routes[-1][0] == u and routes[-1][1] == v:
                cost += routes[-1][2]
            else:
                for i in range(len(routes) - 1):
                    if routes[i][0] == u and routes[i][1] == v:
                        cost += routes[i][2]
                    elif routes[i][0] == v and routes[i][1] == u:
                        cost += routes[i][2]
                    elif routes[i][0] == u and routes[i][1] == routes[i+1][1]:
                        cost += routes[i][2]
                    elif routes[i][0] == v and routes[i][1] == routes[i+1][1]:
                        cost += routes[i][2]
                    elif routes[i][0] == routes[i+1][0] and routes[i][1] == u:
                        cost += routes[i][2]
                    elif routes[i][0] == routes[i+1][0] and routes[i][1] == v:
                        cost += routes[i][2]
                    elif routes[i][0] == routes[i+1][1] and routes[i][1] == u:
                        cost += routes[i][2]
                    elif routes[i][0] == routes[i+1][1] and routes[i][1] == v:
                        cost += routes[i][2]

        return cost

    for assignment in assignments:
        cost = calculate_cost(assignment)
        min_cost = min(min_cost, cost)

    print(min_cost)

