def solve():
    n = int(input())
    edges = []
    for _ in range(n - 1):
        a, b = map(int, input().split())
        edges.append((a, b))

    def check Connectivity(adj, s):
        visited = [False] * n
        stack = [s]
        while stack:
            v = stack.pop()
            if visited[v]:
                continue
            visited[v] = True
            for u in adj[v]:
                if not visited[u]:
                    stack.append(u)
        return not any(v == s for v in visited)

    def find_min_dist(start, end, adj):
        dist_matrix = [[float('inf')] * n for _ in range(n)]
        for s in range(n):
            dist_matrix[s][s] = 0
            q = [(s, 0)]
            while q:
                curr, dist = q.pop(0)
                if curr == end:
                    return dist
                for neighbor in adj[curr]:
                    if dist + dist_matrix[s][neighbor] < dist_matrix[s][end]:
                        dist_matrix[s][end] = dist + dist_matrix[s][neighbor]
                        q.append((neighbor, dist + dist_matrix[s][neighbor]))
        return float('inf')

    def find_optimal_roots(start, end, adj):
        dist_start_end = find_min_dist(start, end, adj)

        if dist_start_end == float('inf'):
            return None

        roots = [start]
        curr = end
        visited = {start}
        adj_copy = {v: [] for v in range(n)}
        for v in adj_copy:
            adj_copy[v].clear()

        while roots:
            curr = roots[-1]
            for _ in range(10):  # Try 10 different points
                for i in range(n):
                    if i not in visited:
                        next_node = i
                        new_dist = dist_start_end + dist_matrix[curr][next_node]
                        if new_dist < dist_matrix[curr][next_node]:
                            dist_matrix[curr][next_node] = new_dist
                            roots.append(next_node)
                            visited.add(next_node)
                            adj_copy[next_node].append(curr)
                            break
                if not roots:
                    break
            if not roots:
                break
        return roots

    start_node = 0
    end_node = n - 1
    optimal_roots = find_optimal_roots(start_node, end_node, edges)

    if optimal_roots:
        for i in range(n):
            print(*optimal_roots[i], *optimal_roots[n - 1 - i])
    else:
        print("No solution")

