import heapq

def solve():
    n, m, alpha = map(int, input().split())
    edges = []
    for _ in range(m):
        u, v, c = map(int, input().split())
        edges.append((u, v, c))

    dist = [float('inf')] * n
    dist[0] = 0
    pq = [(0, 0)]  # (distance, junction)]

    while pq:
        dist_val, curr_junction = heapq.heappop(pq)

        if dist_val > dist[curr_junction]:
            continue

        for neighbor, candy_price in edges:
            road_cost = candy_price - dist[curr_junction]
            if road_cost <= 0:
                continue

            new_dist = dist_val + road_cost**2 + alpha * len(heapq.heappop(pq))

            if new_dist < dist[neighbor]:
                dist[neighbor] = new_dist
                heapq.heappush(pq, (new_dist, neighbor))

    if dist[-1] == float('inf'):
        print("Poor girl")
    else:
        print(dist[-1])

solve()

import math

def distance(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)

def pan(grip1, grip2):
    return distance(grip2[0:], grip2[1:]) / (len(grip2) - 1)

def zoom(grip1, grip2):
    return grip2[1] - grip1[1]

def rotate(grip1, grip2):
    angles = []
    for i in range(len(grip2)):
        angles.append(math.atan2(grip2[i+1][1] - grip2[i][1], grip2[i+1][0] - grip2[i][0]))
    return sum(angles) / len(angles)

def classify(grip1, grip2):
    dist = [pan(grip1, grip2), zoom(grip1, grip2), rotate(grip1, grip2)]
    max_dist = max(dist)
    if max_dist < distance(grip2[0:], grip2[1:]) / (len(grip2) - 1):
        return "pan"
    elif max_dist == distance(grip1, grip2):
        return "zoom"
    else:
        return "rotate"

