def modPower(x, y, mod):
    res = 1
    x = x % mod
    while y > 0:
        if y % 2 == 1:
            res = (res * x) % mod
        y = y >> 1
        x = (x * x) % mod
    return res

def inv(n, mod):
    return modPower(n, mods - 2, mods)

