    n, k = map(int, input().split())
    t = []
    for _ in range(n):
        t.append(int(input()))

    def calculate_time(current_people, cars):
        time = 0
        for people in current_people:
            if people > 0:
                min_time = float('inf')
                for car in cars:
                    min_time = min(min_time, car[0])

                    # Option 1: Drive alone
                    if people <= k:
                        time += people * cars[people-1][0]
                        people = 0
                    else:
                        time += car[0]
                        people -= k
                        break

                    # Option 2: Drive with a passenger
                    if people >= 5:
                        driver_time = cars[people-1][0]
                        passenger_time = people // 5 * cars[people-1][1]
                        total_time = driver_time + passenger_time
                        time += total_time
                        people = 0
                    else:
                        time += car[0]
                        people -= k
                time += min_time
        return time

    cars = []
    for _ in range(k):
        cars.append((t[0], t[0]))
    for _ in range(n - k):
        cars.append((t[-1], t[-1]))

    min_time = calculate_time([0] + list(current_people), cars)
    print(min_time)

