def solve():
    n = int(input())
    if n == 1:
        print("YES" if input() == "U0R0" else "NO")
        return

    packages = []
    for _ in range(n):
        packages.append(list(map(int, input().split())))

    directions = [(0, 1, 'R'), (1, 0, 'U')]

    def calculate_distance(start, end):
        x0, y0 = start
        x1, y1 = packages[-1]
        x2, y2 = end

        dist = abs(x1 - x0) + abs(y1 - y0) + abs(x2 - x1) + abs(y2 - y1)
        return dist

    def find_lexicographically_smallest_path(current_position, end_position, visited):
        if current_position == end_position:
            path = ""
            count = 0
            while current_position != end_position:
                path += "U"
                current_position[0] += 1
                count += 1
            path += "R" * count
            return path

        possible_directions = []
        for direction in directions:
            if direction[0] <= current_position[0] and direction[0] >= 0 and direction[1] <= current_position[1] and direction[1] >= 0:
                possible_directions.append(direction)

        if not possible_directions:
            return ""

        min_distance = float('inf')
        result_path = ""

        for direction in possible_directions:
            new_position = [current_position[0] + direction[0], current_position[1] + direction[1]]

            if new_position[0] >= 0 and new_position[1] >= 0 and new_position not in visited:
                distance = calculate_distance(current_position, new_position)
                if distance < min_distance:
                    min_distance = distance
                    result_path = "U"
                    for _ in range(distance):
                        result_path += "R"
                elif distance == min_distance:
                    result_path += "R"
                    for _ in range(distance - 1):
                        result_path += "U"

            visited.add(new_position)

        return result_path

    start_position = [0, 0]
    end_position = packages[-1]

    result = find_lexicographically_smallest_path(start_position, end_position, set())

    if result == "":
        print("NO")
    else:
        print("YES")
        print(result)

