def solve():
    n, m = map(int, input().split())
    grid = [input() for _ in range(n)]

    def is_valid(x, y):
        if 0 <= x < n and 0 <= y < m:
            return grid[x][y] != '#'
        return False

    def get_adjacent(x, y):
        adjacent = []
        if x > 0:
            adjacent.append((x - 1, y))
        if x < n - 1:
            adjacent.append((x + 1, y))
        if y > 0:
            adjacent.append((x, y - 1))
        if y < m - 1:
            adjacent.append((x, y + 1))
        return adjacent

    def place_shops(x, y):
        grid[x][y] = 'E'
        for dx, dy in get_adjacent(x, y):
            if is_valid(dx, dy):
                place_shops(dx, dy)

    for x in range(n):
        for y in range(m):
            if grid[x][y] == '#':
                place_shops(x, y)
                break

    print('\n'.join(grid))

