        n = len(arr)
        for i in range(n - 1):
            if arr[i] > arr[i + 1]:
                return False
        return True

    def allowed_swaps(index1, index2):
        if index1 + 1 == index2:
            return []

        p1 = p[index1]
        p2 = p[index2] - 1

        swaps = []
        if index1 < p1:
            swaps.append((index1, p1))
        if index1 > p1:
            temp_index = index1
            index1 = p1
            p1 = temp_index
            index2 = p2
            p2 = temp_index - 1
            temp = index2
            index2 = index1 - 1
            index1 = temp
        if index2 < p2:
            swaps.append((index2, p2))
        if index2 > p2:
            temp = index2
            index2 = p2
            temp = index2 - 1
            index2 = temp
            p2 = temp - 1
            index1 = p1
            p1 = temp
        return swaps

    def is_valid(index, swaps):
        new_arr = a[:]
        for swap in swaps:
            new_index1, new_index2 = swap
            if (new_index1, new_index2) == (index, index + 1):
                new_arr[index], new_arr[new_index2] = new_arr[new_index2], new_arr[index]
        return can_sort(new_arr)

    for i in range(m):
        result_swaps = allowed_swaps(p[i] - 1, p[i] + 1)
        result_valid = is_valid(i, result_swaps)
