def solve():
    n, m = map(int, input().split())
    edges = []
    for _ in range(m):
        u, v = map(int, input().split())
        edges.append((u, v))

    def check(graph):
        for u, v in edges:
            if graph[u][v] == 0:
                graph[u][v] = 1
                graph[v][u] = 1
        for i in range(1, len(graph)):
            if sum(graph[i]) > 1):
                return False
        return True

    def get Orientation(s, t):
        count = 0
        dist = [0] * (n + 1)
        dist[s] = 1
        q = [s]
        visited = [False] * (n + 1)
        while q:
            u = q.pop(0)
            if u == t:
                return count
            if u == s:
                return count + 1
            for v in range(1, n + 1):
                if dist[v] == 0 and v != t:
                    if s != t and (v == s or graph[s][v]):
                        continue
                    if visited[v]:
                        continue
                    visited[v] = True
                    dist[v] = dist[u] + 1
                    q.append(v)
        return -1

    def construct_answer(edges, graph):
        answer = ['0'] * m
        s = 0
        t = n
        for u, v in edges:
            if graph[u][v] == 1:
                answer[get(Orientations(s, u), get(Orientations(t, v)))] = '1'
                s = get(Orientations(s, u))
                t = get(Orientations(t, v))
        return "".join(answer)

    def Orientations(node, rev):
        if rev:
            return get(Orientations(node, node+1))
        else:
            graph = [[0] * (n + 1) for _ in range(n + 1)]
            for u, v in edges:
                if u == node and v == node + 1:
                    graph[node][v] = 1
                elif u == node + 1 and v == node:
                    graph[u][v] = 1
                elif u == node and v == node - 1:
                    graph[u][v] = 1
                elif u == node + 1 and v == node - 1:
                    graph[u][v] = 1
                elif u == node - 1 and v == node:
                    graph[u][v] = 1
                elif u == node - 1 and v == node + 1:
                    graph[u][v] = 1
            return get(Orientations(node - 1, node))

